namespace gamboamartin\cat_sat\controllers;

use gamboamartin\cat_sat\models\cat_sat_periodicidad_pago_nom;
use gamboamartin\errores\errores;
use gamboamartin\system\_ctl_base;
use gamboamartin\system\links_menu;
use gamboamartin\template\html;
use html\cat_sat_periodicidad_pago_nom_html;
use PDO;
use stdClass;

class controlador_cat_sat_periodicidad_pago_nom extends _ctl_base {

    public function __construct(PDO $link, html $html = new \gamboamartin\template_1\html(),
                                stdClass $paths_conf = new stdClass()){

        $modelo = new cat_sat_periodicidad_pago_nom(link: $link);
        $html_ = new cat_sat_periodicidad_pago_nom_html(html: $html);
        $obj_link = new links_menu(link: $link, registro_id: $this->registro_id);

        $datatables = new stdClass();
        $datatables->columns = array();
        $datatables->columns['cat_sat_periodicidad_pago_nom_id']['titulo'] = 'Id';
        $datatables->columns['cat_sat_periodicidad_pago_nom_codigo']['titulo'] = 'Cod';
        $datatables->columns['cat_sat_periodicidad_pago_nom_descripcion']['titulo'] = 'Periodicidad Pago';
        $datatables->columns['cat_sat_periodicidad_pago_nom_n_dias']['titulo'] = 'Dias';

        $datatables->filtro = array();
        $datatables->filtro[] = 'cat_sat_periodicidad_pago_nom.id';
        $datatables->filtro[] = 'cat_sat_periodicidad_pago_nom.codigo';
        $datatables->filtro[] = 'cat_sat_periodicidad_pago_nom.descripcion';
        $datatables->filtro[] = 'cat_sat_periodicidad_pago_nom.n_dias';

        parent::__construct(html: $html_, link: $link, modelo: $modelo, obj_link: $obj_link, datatables: $datatables,
            paths_conf: $paths_conf);

        $this->titulo_lista = 'Periocidad Pago';
        $this->lista_get_data = true;

    }

    public function alta(bool $header, bool $ws = false): array|string
    {
        $r_alta = $this->init_alta();
        if(errores::$error){
            return $this->retorno_error(
                mensaje: 'Error al inicializar alta',data:  $r_alta, header: $header,ws:  $ws);
        }

        $keys_selects = array('codigo','descripcion','n_dias');
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al maquetar key_selects',data:  $keys_selects, header: $header,ws:  $ws);
        }

        $inputs = $this->inputs(keys_selects: $keys_selects);
        if(errores::$error){
            return $this->retorno_error(
                mensaje: 'Error al obtener inputs',data:  $inputs, header: $header,ws:  $ws);
        }

        return $r_alta;
    }

    protected function campos_view(): array
    {
        $keys = new stdClass();
        $keys->inputs = array('codigo','descripcion','n_dias');
        $keys->selects = array();

        $campos_view = (new \base\controller\init())->model_init_campos_template(
            campos_view: array(),keys:  $keys, link: $this->link);

        if(errores::$error){
            return $this->errores->error(mensaje: 'Error al inicializar campo view',data:  $campos_view);
        }

        return $campos_view;
    }

    protected function key_selects_txt(array $keys_selects): array
    {

        $keys_selects = (new \base\controller\init())->key_select_txt(
            cols: 12,key: 'codigo', keys_selects:$keys_selects, place_holder: 'Cod');
        if(errores::$error){
            return $this->errores->error(mensaje: 'Error al maquetar key_selects',data:  $keys_selects);
        }

        $keys_selects = (new \base\controller\init())->key_select_txt(cols: 6,key: 'descripcion',
            keys_selects:$keys_selects, place_holder: 'Periodicidad Pago');
        if(errores::$error){
            return $this->errores->error(mensaje: 'Error al maquetar key_selects',data:  $keys_selects);
        }

        $keys_selects = (new \base\controller\init())->key_select_txt(cols: 6,key: 'n_dias',
            keys_selects:$keys_selects, place_holder: 'Dias del periodo');
        if(errores::$error){
            return $this->errores->error(mensaje: 'Error al maquetar key_selects',data:  $keys_selects);
        }

        return $keys_selects;
    }

    public function modifica(
        bool $header, bool $ws = false): array|stdClass
    {
        $r_modifica = $this->init_modifica(); // TODO: Change the autogenerated stub
        if(errores::$error){
            return $this->retorno_error(
                mensaje: 'Error al generar salida de template',data:  $r_modifica,header: $header,ws: $ws);
        }



        $base = $this->base_upd(keys_selects: array(), params: array(),params_ajustados: array());
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al integrar base',data:  $base, header: $header,ws:  $ws);
        }


        return $r_modifica;
    }

}
