namespace gamboamartin\cat_sat\controllers;

use base\controller\init;
use gamboamartin\cat_sat\models\cat_sat_tipo_impuesto;
use gamboamartin\errores\errores;
use gamboamartin\system\_ctl_parent;
use gamboamartin\system\links_menu;
use gamboamartin\template\html;
use html\cat_sat_tipo_impuesto_html;
use PDO;
use stdClass;

class controlador_cat_sat_tipo_impuesto extends _ctl_parent {

    public function __construct(PDO $link, html $html = new \gamboamartin\template_1\html(),
                                stdClass $paths_conf = new stdClass()){
        $modelo = new cat_sat_tipo_impuesto(link: $link);
        $html_ = new cat_sat_tipo_impuesto_html(html: $html);
        $obj_link = new links_menu(link: $link, registro_id: $this->registro_id);

        $columns["cat_sat_tipo_impuesto_id"]["titulo"] = "Id";
        $columns["cat_sat_tipo_impuesto_codigo"]["titulo"] = "Codigo";
        $columns["cat_sat_tipo_impuesto_descripcion"]["titulo"] = "Tipo Impuesto";


        $filtro = array("cat_sat_tipo_impuesto.id", "cat_sat_tipo_impuesto.descripcion","cat_sat_tipo_impuesto.codigo");

        $datatables = new stdClass();
        $datatables->columns = $columns;
        $datatables->filtro = $filtro;

        parent::__construct(html: $html_, link: $link, modelo: $modelo, obj_link: $obj_link, datatables: $datatables, paths_conf: $paths_conf);

        $this->titulo_lista = 'Tipo Impuesto';
        $this->lista_get_data = true;

    }

    public function alta(bool $header, bool $ws = false): array|string
    {
        $r_alta = parent::alta(header: false); // TODO: Change the autogenerated stub
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar template',data:  $r_alta,header:  $header,ws:  $ws);
        }
        $codigo = $this->html->input_codigo(cols: 6,row_upd: new stdClass(),value_vacio: true);
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar codigo',data:  $codigo,header:  $header,ws:  $ws);
        }
        $this->inputs->codigo = $codigo;

        $descripcion = $this->html->input_descripcion(cols: 6,row_upd: new stdClass(),value_vacio: true,
            place_holder: 'Tipo Impuesto');
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar codigo',data:  $codigo,header:  $header,ws:  $ws);
        }
        $this->inputs->descripcion = $descripcion;

        return $r_alta;
    }

    /**
     * Inicializa los parametros para mostrado en alta y modifica
     * @param array $keys_selects
     * @return array
     */
    protected function key_selects_txt(array $keys_selects): array
    {

        $keys_selects = (new init())->key_select_txt(cols: 6,key: 'descripcion',
            keys_selects:$keys_selects, place_holder: 'Tipo Impuesto');
        if(errores::$error){
            return $this->errores->error(mensaje: 'Error al maquetar key_selects',data:  $keys_selects);
        }
        $keys_selects = (new init())->key_select_txt(cols: 6,key: 'codigo',
            keys_selects:$keys_selects, place_holder: 'Codigo');
        if(errores::$error){
            return $this->errores->error(mensaje: 'Error al maquetar key_selects',data:  $keys_selects);
        }

        return $keys_selects;
    }



}
