namespace gamboamartin\comercial\controllers;

use base\controller\controler;
use gamboamartin\comercial\models\com_email_cte;
use gamboamartin\errores\errores;
use gamboamartin\system\_ctl_parent_sin_codigo;
use gamboamartin\system\links_menu;
use gamboamartin\template\html;
use html\com_cliente_html;
use html\com_email_cte_html;
use PDO;
use stdClass;

class controlador_com_email_cte extends _base_sin_cod {

    public array|stdClass $keys_selects = array();
    public controlador_com_sucursal $controlador_com_sucursal;

    public function __construct(PDO $link, html $html = new \gamboamartin\template_1\html(),
                                stdClass $paths_conf = new stdClass()){
        $modelo = new com_email_cte(link: $link);
        $html_ = new com_email_cte_html(html: $html);
        parent::__construct(html_: $html_,link:  $link,modelo:  $modelo, paths_conf: $paths_conf);


    }

    public function alta(bool $header, bool $ws = false): array|string
    {
        $r_alta =  parent::alta($header, $ws); // TODO: Change the autogenerated stub
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar template', data: $r_alta, header: $header,ws:  $ws);
        }

        $com_cliente_id = (new com_cliente_html(html: $this->html_base))->select_com_cliente_id(
            cols: 12,con_registros: true,id_selected: -1,link:  $this->link);

        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar input', data: $com_cliente_id, header: $header,ws:  $ws);
        }

        $this->inputs->com_cliente_id = $com_cliente_id;

        $descripcion = (new com_email_cte_html(html: $this->html_base))->input_email(cols: 12,row_upd:  new stdClass(),
            value_vacio:  false, name: 'descripcion');

        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar input', data: $descripcion, header: $header,ws:  $ws);
        }

        $this->inputs->descripcion = $descripcion;


        return $r_alta;
    }

    private function init_controladores(stdClass $paths_conf): controler
    {
        $this->controlador_com_sucursal= new controlador_com_sucursal(link:$this->link, paths_conf: $paths_conf);

        return $this;
    }

    public function init_datatable(): stdClass
    {
        $datatables = new stdClass();
        $datatables->columns = array();
        $datatables->columns['com_email_cte_id']['titulo'] = 'Id';
        $datatables->columns['com_email_cte_descripcion']['titulo'] = 'Email';
        $datatables->columns['com_cliente_razon_social']['titulo'] = 'Cliente';

        $datatables->filtro = array();
        $datatables->filtro[] = 'com_email_cte.id';
        $datatables->filtro[] = 'com_email_cte.descripcion';
        $datatables->filtro[] = 'com_cliente.razon_social';

        return $datatables;
    }

    public function modifica(bool $header, bool $ws = false, array $keys_selects = array()): array|stdClass
    {
        $r_modifica = parent::modifica($header, $ws, $keys_selects); // TODO: Change the autogenerated stub
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar template', data: $r_modifica, header: $header,ws:  $ws);
        }

        $com_cliente_id = (new com_cliente_html(html: $this->html_base))->select_com_cliente_id(
            cols: 12,con_registros: true,id_selected: $this->row_upd->com_cliente_id,link:  $this->link);

        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar input', data: $com_cliente_id, header: $header,ws:  $ws);
        }

        $this->inputs->com_cliente_id = $com_cliente_id;

        $descripcion = (new com_email_cte_html(html: $this->html_base))->input_email(cols: 12,row_upd:  $this->row_upd,
            value_vacio:  false, name: 'descripcion');

        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar input', data: $descripcion, header: $header,ws:  $ws);
        }

        $this->inputs->descripcion = $descripcion;

        return $r_modifica;

    }


}
