<?php
namespace gamboamartin\comercial\models;
use base\orm\_modelo_parent;
use base\orm\_modelo_parent_sin_codigo;
use gamboamartin\errores\errores;
use PDO;
use stdClass;

class com_tipo_contacto extends _modelo_parent_sin_codigo{
    public function __construct(PDO $link, array $childrens = array()){
        $tabla = 'com_tipo_contacto';
        $columnas = array($tabla=>false);
        $campos_obligatorios = array();
        $childrens['com_tipo_contacto'] ="gamboamartin\comercial\models";

        $columnas_extra['com_tipo_contacto_n_contactos'] =
            "(SELECT COUNT(*) FROM com_contacto WHERE com_contacto.com_tipo_contacto_id = com_tipo_contacto.id)";

        parent::__construct(link: $link, tabla: $tabla, campos_obligatorios: $campos_obligatorios,
            columnas: $columnas, columnas_extra: $columnas_extra, childrens: $childrens);

        $this->NAMESPACE = __NAMESPACE__;

        $this->etiqueta = 'Tipo de contacto';

    }

    public function alta_bd(array $keys_integra_ds = array('codigo', 'descripcion')): array|stdClass
    {
        $validacion = $this->validacion(datos: $this->registro,registro_id: -1);
        if (errores::$error) {
            return $this->error->error(mensaje: 'Error de validacion', data: $validacion);
        }

        $alta = parent::alta_bd($keys_integra_ds); // TODO: Change the autogenerated stub
        if (errores::$error) {
            return $this->error->error(mensaje: 'Error de validacion', data: $alta);
        }
        return $alta;
    }

    public function validacion(array $datos, int $registro_id): array|stdClass
    {
        if(array_key_exists('status', $datos)){
            return $datos;
        }

        if (strlen($datos['descripcion']) < 3) {
            $mensaje_error = sprintf("Error el tipo de contacto '%s' debe tener como minimo 3 caracteres",
                $datos['descripcion']);
            return $this->error->error(mensaje: $mensaje_error, data: $datos);
        }

        $filtro['com_tipo_contacto.descripcion'] = trim($datos['descripcion']);
        $filtro_extra[0]['com_tipo_contacto.id']['operador'] = '!=';
        $filtro_extra[0]['com_tipo_contacto.id']['comparacion'] = 'AND';
        $filtro_extra[0]['com_tipo_contacto.id']['valor'] = $registro_id;
        $existe = $this->filtro_and(filtro: $filtro, filtro_extra: $filtro_extra);
        if (errores::$error) {
            return $this->error->error(mensaje: 'Error de duplcacion', data: $existe);
        }

        if ($existe->n_registros > 0) {
            $mensaje_error = sprintf("Error el tipo de contacto '%s' ya existe", $datos['descripcion']);
            return $this->error->error(mensaje: $mensaje_error, data: $datos);
        }

        return $existe;
    }

    public function modifica_bd(array $registro, int $id, bool $reactiva = false,
                                array $keys_integra_ds = array('descripcion')): array|stdClass
    {
        $validacion = $this->validacion(datos: $registro, registro_id: $id);
        if (errores::$error) {
            return $this->error->error(mensaje: 'Error de validacion', data: $validacion);
        }

        $modifica = parent::modifica_bd($registro, $id, $reactiva, $keys_integra_ds);
        if (errores::$error) {
            return $this->error->error(mensaje: 'Error de validacion', data: $validacion);

        }
        return $modifica;
    }

    public function contactos(int $com_tipo_contacto_id): array
    {
        if($com_tipo_contacto_id <= 0){
            return $this->error->error(mensaje: 'Error com_tipo_contacto_id debe ser mayor a 0',data:  $com_tipo_contacto_id);
        }

        $filtro['com_tipo_contacto.id'] = $com_tipo_contacto_id;

        $data = (new com_contacto($this->link))->filtro_and(filtro: $filtro);
        if(errores::$error){
            return $this->error->error(mensaje: 'Error al obtener contactos',data:  $data);
        }
        return $data->registros;
    }
}