namespace gamboamartin\facturacion\controllers;

use gamboamartin\cat_sat\models\cat_sat_motivo_cancelacion;
use gamboamartin\errores\errores;
use gamboamartin\facturacion\html\fc_cancelacion_html;
use gamboamartin\facturacion\html\fc_factura_html;
use gamboamartin\facturacion\models\fc_cancelacion;
use gamboamartin\facturacion\models\fc_factura;
use gamboamartin\system\links_menu;
use gamboamartin\system\system;
use gamboamartin\template\html;
use html\cat_sat_motivo_cancelacion_html;
use PDO;
use stdClass;

class controlador_fc_cancelacion extends system{

    public array|stdClass $keys_selects = array();



    public function __construct(PDO $link, html $html = new \gamboamartin\template_1\html(),
                                stdClass $paths_conf = new stdClass()){
        $modelo = new fc_cancelacion(link: $link);
        $html_ = new fc_cancelacion_html(html: $html);
        $obj_link = new links_menu(link: $link, registro_id:  $this->registro_id);


        $datatables = $this->init_datatable();
        if(errores::$error){
            $error = $this->errores->error(mensaje: 'Error al inicializar datatable',data: $datatables);
            print_r($error);
            die('Error');
        }

        parent::__construct(html:$html_, link: $link,modelo:  $modelo, obj_link: $obj_link, datatables: $datatables,
            paths_conf: $paths_conf);





        $this->lista_get_data = true;

        $this->parents_verifica[] = (new fc_factura(link: $this->link));
        $this->parents_verifica[] = (new cat_sat_motivo_cancelacion(link: $this->link));


        $this->verifica_parents_alta = false;

    }

    public function alta(bool $header, bool $ws = false): array|string
    {
        $r_alta =  parent::alta(header: false); // TODO: Change the autogenerated stub
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar template',data:  $r_alta, header: $header,ws:  $ws);
        }

        $fc_factura_id = (new fc_factura_html(html: $this->html_base))->select_fc_factura_id(
            cols:12,con_registros: true,id_selected: -1,link: $this->link);
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar select factura',data:  $fc_factura_id,
                header: $header,ws:  $ws);
        }

        $cat_sat_motivo_cancelacion_id =
            (new cat_sat_motivo_cancelacion_html(html: $this->html_base))->select_cat_sat_motivo_cancelacion_id(
                cols: 12,con_registros: true,id_selected: -1,link: $this->link);
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar select cat_sat_motivo_cancelacion_id',
                data:  $cat_sat_motivo_cancelacion_id, header: $header,ws:  $ws);
        }

        $this->inputs = new stdClass();
        $this->inputs->fc_factura_id = $fc_factura_id;
        $this->inputs->cat_sat_motivo_cancelacion_id = $cat_sat_motivo_cancelacion_id;


        return $r_alta;
    }

    final public function init_datatable(): stdClass
    {

        $columns["fc_factura_folio"]["titulo"] = "Fol";
        $columns["cat_sat_motivo_cancelacion_descripcion"]["titulo"] = "Motivo";
        $columns["com_cliente_rfc"]["titulo"] = "Cte";

        $filtro = array("fc_factura.folio","cat_sat_motivo_cancelacion.descripcion", "com_cliente.rfc");

        $datatables = new stdClass();
        $datatables->columns = $columns;
        $datatables->filtro = $filtro;

        return $datatables;
    }



}
