namespace gamboamartin\facturacion\controllers;


use gamboamartin\comercial\models\com_email_cte;
use gamboamartin\errores\errores;
use gamboamartin\facturacion\html\fc_receptor_email_html;
use gamboamartin\facturacion\models\fc_receptor_email;
use gamboamartin\notificaciones\html\not_receptor_html;
use gamboamartin\notificaciones\models\not_receptor;
use gamboamartin\system\links_menu;
use gamboamartin\system\system;
use gamboamartin\template\html;
use html\com_email_cte_html;
use PDO;
use stdClass;

class controlador_fc_receptor_email extends system{

    public array|stdClass $keys_selects = array();



    public function __construct(PDO $link, html $html = new \gamboamartin\template_1\html(),
                                stdClass $paths_conf = new stdClass()){
        $modelo = new fc_receptor_email(link: $link);
        $html_ = new fc_receptor_email_html(html: $html);
        $obj_link = new links_menu(link: $link, registro_id:  $this->registro_id);


        $datatables = $this->init_datatable();
        if(errores::$error){
            $error = $this->errores->error(mensaje: 'Error al inicializar datatable',data: $datatables);
            print_r($error);
            die('Error');
        }

        parent::__construct(html:$html_, link: $link,modelo:  $modelo, obj_link: $obj_link, datatables: $datatables,
            paths_conf: $paths_conf);


        $this->lista_get_data = true;

        $this->parents_verifica[] = (new not_receptor(link: $this->link));
        $this->parents_verifica[] = (new com_email_cte(link: $this->link));


        $this->verifica_parents_alta = false;

    }


    final public function init_datatable(): stdClass
    {

        $columns["fc_receptor_email_id"]["titulo"] = "Id";
        $columns["com_email_cte_descripcion"]["titulo"] = "Correo Cte";
        $columns["not_receptor_email"]["titulo"] = "Correo Rec";

        $filtro = array("fc_receptor_email.id","com_email_cte.descripcion", "not_receptor.email");

        $datatables = new stdClass();
        $datatables->columns = $columns;
        $datatables->filtro = $filtro;

        return $datatables;
    }

    public function alta(bool $header, bool $ws = false): array|string
    {
        $r_alta =  parent::alta($header, $ws); // TODO: Change the autogenerated stub
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al insertar',data:  $r_alta,header:  $header,ws:  $ws);
        }
        $not_receptor_id = (new not_receptor_html(html: $this->html_base))->select_not_receptor_id(cols:12,
            con_registros:  true,id_selected: -1,link: $this->link);
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar input',data:  $not_receptor_id,header:  $header,ws:  $ws);
        }

        $com_email_cte_id = (new com_email_cte_html(html: $this->html_base))->select_com_email_cte_id(cols:12,
            con_registros:  true,id_selected: -1,link: $this->link);
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar input',data:  $com_email_cte_id,header:  $header,ws:  $ws);
        }

        $this->inputs = new stdClass();
        $this->inputs->not_receptor_id = $not_receptor_id;
        $this->inputs->com_email_cte_id = $com_email_cte_id;
        return $r_alta;
    }

    public function modifica(bool $header, bool $ws = false): array|stdClass
    {
        $r_modifica = parent::modifica($header, $ws); // TODO: Change the autogenerated stub
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar template',data:  $r_modifica,header:  $header,ws:  $ws);
        }
        $not_receptor_id = (new not_receptor_html(html: $this->html_base))->select_not_receptor_id(cols:12,
            con_registros:  true,id_selected: $this->row_upd->not_receptor_id,link: $this->link);
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar input',data:  $not_receptor_id,header:  $header,ws:  $ws);
        }

        $com_email_cte_id = (new com_email_cte_html(html: $this->html_base))->select_com_email_cte_id(cols:12,
            con_registros:  true,id_selected: $this->row_upd->com_email_cte_id,link: $this->link);
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar input',data:  $com_email_cte_id,header:  $header,ws:  $ws);
        }

        $this->inputs = new stdClass();
        $this->inputs->not_receptor_id = $not_receptor_id;
        $this->inputs->com_email_cte_id = $com_email_cte_id;

        return $r_modifica;
    }


}
