namespace gamboamartin\facturacion\controllers;

use base\controller\controler;
use gamboamartin\errores\errores;
use gamboamartin\facturacion\html\fc_complemento_pago_html;
use gamboamartin\facturacion\html\fc_relacion_cp_html;
use gamboamartin\facturacion\models\fc_conf_etapa_rel;
use gamboamartin\facturacion\models\fc_complemento_pago;
use gamboamartin\facturacion\models\fc_relacion_cp;

use gamboamartin\template\html;
use html\cat_sat_tipo_relacion_html;
use PDO;
use stdClass;

class controlador_fc_relacion_cp extends _base {


    public function __construct(PDO      $link, html $html = new \gamboamartin\template_1\html(),
                                stdClass $paths_conf = new stdClass())
    {
        $modelo = new fc_relacion_cp(link: $link);
        $html_ = new fc_relacion_cp_html(html: $html);
        parent::__construct(html_: $html_,link:  $link,modelo:  $modelo, paths_conf: $paths_conf);


    }

    public function alta(bool $header, bool $ws = false): array|string
    {
        $r_alta = parent::alta($header, $ws); // TODO: Change the autogenerated stub
        if (errores::$error) {
            $error = $this->errores->error(mensaje: 'Error al generar template', data: $r_alta);
            print_r($error);
            die('Error');
        }

        $fc_configuraciones_etapa_rel = (new fc_conf_etapa_rel(link: $this->link))->registros_activos();
        if(errores::$error){
            $error = $this->errores->error(mensaje: 'Error al obtener fc_conf_etapa_rel', data: $fc_configuraciones_etapa_rel);
            print_r($error);
            die('Error');
        }

        $fc_complemento_pago_id_get = -1;
        if(isset($_GET['fc_complemento_pago_id'])){
            $fc_complemento_pago_id_get = $_GET['fc_complemento_pago_id'];
        }


        $facturas = (new fc_complemento_pago(link: $this->link))->registros_activos();
        if (errores::$error) {
            $error = $this->errores->error(mensaje: 'Error al obtener facturas', data: $facturas);
            print_r($error);
            die('Error');
        }



        foreach ($facturas as $indice=>$factura){
            $aplica_relacion = false;
            foreach ($fc_configuraciones_etapa_rel as $fc_configuracion_etapa_rel){
                if($factura['fc_complemento_pago_etapa'] === $fc_configuracion_etapa_rel['pr_etapa_descripcion']){
                    $aplica_relacion = true;
                    break;
                }
            }
            if(!$aplica_relacion){
                unset($facturas[$indice]);
            }
            if((int)$factura['fc_complemento_pago_id'] !== (int)$fc_complemento_pago_id_get && $fc_complemento_pago_id_get >0){
                unset($facturas[$indice]);
            }
        }

        $con_registros = true;
        if(count($facturas) === 0){
            $con_registros = false;
        }



        $fc_complemento_pago_id = (new fc_complemento_pago_html(html: $this->html_base))->select_fc_complemento_pago_id(cols: 12,
            con_registros: $con_registros, id_selected: $fc_complemento_pago_id_get, link: $this->link, registros: $facturas);
        if (errores::$error) {
            $error = $this->errores->error(mensaje: 'Error al generar input', data: $fc_complemento_pago_id);
            print_r($error);
            die('Error');
        }

        $cat_sat_tipo_relacion_id = (new cat_sat_tipo_relacion_html(html: $this->html_base))->select_cat_sat_tipo_relacion_id(cols: 12,
            con_registros: true, id_selected: -1, link: $this->link);
        if (errores::$error) {
            $error = $this->errores->error(mensaje: 'Error al generar input', data: $cat_sat_tipo_relacion_id);
            print_r($error);
            die('Error');
        }

        $this->inputs = new stdClass();
        $this->inputs->fc_complemento_pago_id = $fc_complemento_pago_id;
        $this->inputs->cat_sat_tipo_relacion_id = $cat_sat_tipo_relacion_id;


        return $r_alta;

    }


    private function init_configuraciones(): controler
    {
        $this->seccion_titulo = 'Relaciones';
        $this->titulo_lista = 'Registro de Relaciones';

        return $this;
    }



    private function init_datatable(): stdClass
    {
        $columns["fc_complemento_pago_id"]["titulo"] = "Id";
        $columns["fc_complemento_pago_folio"]["titulo"] = "Folio";
        $columns["cat_sat_tipo_relacion_descripcion"]["titulo"] = "Tipo Relacion";

        $filtro = array("fc_complemento_pago.id","fc_complemento_pago.folio","cat_sat_tipo_relacion.descripcion");

        $datatables = new stdClass();
        $datatables->columns = $columns;
        $datatables->filtro = $filtro;

        return $datatables;
    }

    public function modifica(bool $header, bool $ws = false): array|stdClass
    {
        $r_modifica = parent::modifica($header, $ws); // TODO: Change the autogenerated stub
        if (errores::$error) {
            $error = $this->errores->error(mensaje: 'Error al generar template', data: $r_modifica);
            print_r($error);
            die('Error');
        }

        $fc_configuraciones_etapa_rel = (new fc_conf_etapa_rel(link: $this->link))->registros_activos();
        if(errores::$error){
            $error = $this->errores->error(mensaje: 'Error al obtener fc_conf_etapa_rel', data: $fc_configuraciones_etapa_rel);
            print_r($error);
            die('Error');
        }


        $facturas = (new fc_complemento_pago(link: $this->link))->registros_activos();
        if (errores::$error) {
            $error = $this->errores->error(mensaje: 'Error al obtener facturas', data: $facturas);
            print_r($error);
            die('Error');
        }

        foreach ($facturas as $indice=>$factura){
            $aplica_relacion = false;
            foreach ($fc_configuraciones_etapa_rel as $fc_configuracion_etapa_rel){
                if($factura['fc_complemento_pago_etapa'] === $fc_configuracion_etapa_rel['pr_etapa_descripcion']){
                    $aplica_relacion = true;
                    break;
                }
            }
            if(!$aplica_relacion){
                unset($facturas[$indice]);
            }
        }

        $con_registros = true;
        if(count($facturas) === 0){
            $con_registros = false;
        }

        $fc_complemento_pago_id = (new fc_complemento_pago_html(html: $this->html_base))->select_fc_complemento_pago_id(cols: 12,
            con_registros: $con_registros, id_selected: $this->row_upd->fc_complemento_pago_id, link: $this->link,
            disabled: true, registros: $facturas);
        if (errores::$error) {
            $error = $this->errores->error(mensaje: 'Error al generar input', data: $fc_complemento_pago_id);
            print_r($error);
            die('Error');
        }

        $cat_sat_tipo_relacion_id = (new cat_sat_tipo_relacion_html(html: $this->html_base))->select_cat_sat_tipo_relacion_id(cols: 12,
            con_registros: true, id_selected: $this->row_upd->cat_sat_tipo_relacion_id, link: $this->link, disabled: true);
        if (errores::$error) {
            $error = $this->errores->error(mensaje: 'Error al generar input', data: $cat_sat_tipo_relacion_id);
            print_r($error);
            die('Error');
        }

        $this->inputs = new stdClass();
        $this->inputs->fc_complemento_pago_id = $fc_complemento_pago_id;
        $this->inputs->cat_sat_tipo_relacion_id = $cat_sat_tipo_relacion_id;
        return $r_modifica;

    }


}
