namespace gamboamartin\facturacion\controllers;

use gamboamartin\errores\errores;
use gamboamartin\facturacion\html\fc_uuid_cancela_html;
use gamboamartin\facturacion\html\fc_uuid_html;
use gamboamartin\facturacion\models\fc_uuid_cancela;
use gamboamartin\system\_ctl_parent_sin_codigo;
use gamboamartin\system\links_menu;
use gamboamartin\template\html;
use html\cat_sat_motivo_cancelacion_html;
use PDO;
use stdClass;


class controlador_fc_uuid_cancela extends _ctl_parent_sin_codigo {


    public function __construct(PDO $link, html $html = new \gamboamartin\template_1\html(), stdClass $paths_conf = new stdClass()){
        $modelo = new fc_uuid_cancela(link: $link);

        $html_ = new fc_uuid_cancela_html(html: $html);
        $obj_link = new links_menu(link: $link, registro_id: $this->registro_id);

        $datatables = new stdClass();
        $datatables->columns = array();
        $datatables->columns['cat_sat_tipo_de_comprobante_descripcion']['titulo'] = 'Tipo de comprobante';
        $datatables->columns['fc_uuid_cancela_id']['titulo'] = 'Id';
        $datatables->columns['fc_uuid_uuid']['titulo'] = 'UUID';
        $datatables->columns['com_cliente_rfc']['titulo'] = 'Cliente';
        $datatables->columns['org_empresa_rfc']['titulo'] = 'Empresa';
        $datatables->columns['fc_uuid_fecha']['titulo'] = 'Fecha';
        $datatables->columns['fc_uuid_folio']['titulo'] = 'Folio';
        $datatables->columns['cat_sat_motivo_cancelacion_descripcion']['titulo'] = 'Motivo de Cancelacion';

        $datatables->filtro = array();
        $datatables->filtro[] = 'cat_sat_tipo_de_comprobante.descripcion';
        $datatables->filtro[] = 'fc_uuid_cancela.id';
        $datatables->filtro[] = 'fc_uuid.uuid';
        $datatables->filtro[] = 'com_cliente.rfc';
        $datatables->filtro[] = 'org_empresa.rfc';
        $datatables->filtro[] = 'fc_uuid.fecha';
        $datatables->filtro[] = 'fc_uuid.folio';
        $datatables->filtro[] = 'cat_sat_motivo_cancelacion.descripcion';

        parent::__construct(html: $html_, link: $link, modelo: $modelo, obj_link: $obj_link, datatables: $datatables,
            paths_conf: $paths_conf);

        $this->titulo_lista = 'Folio Externos Cancelados';


    }

    public function alta(bool $header, bool $ws = false): array|string
    {
        $r_alta = parent::alta($header, $ws); // TODO: Change the autogenerated stub
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar template',data:  $r_alta, header: $header,ws:  $ws);
        }

        $fc_uuid_id = (new fc_uuid_html(html: $this->html_base))->select_fc_uuid_id(
            cols: 12, con_registros: true, id_selected: -1, link: $this->link);

        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar input',data:  $fc_uuid_id, header: $header,ws:  $ws);
        }

        $this->inputs->fc_uuid_id = $fc_uuid_id;

        $cat_sat_motivo_cancelacion_id =
            (new cat_sat_motivo_cancelacion_html(html: $this->html_base))->select_cat_sat_motivo_cancelacion_id(
                cols: 12, con_registros: true, id_selected: -1, link: $this->link);

        if(errores::$error){
            return $this->retorno_error(
                mensaje: 'Error al generar input',data:  $cat_sat_motivo_cancelacion_id, header: $header,ws:  $ws);
        }

        $this->inputs->cat_sat_motivo_cancelacion_id = $cat_sat_motivo_cancelacion_id;

        return $r_alta;
    }

    public function modifica(bool $header, bool $ws = false, array $keys_selects = array()): array|stdClass
    {
        $r_modifica = parent::modifica($header, $ws, $keys_selects); // TODO: Change the autogenerated stub
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar template',data:  $r_modifica, header: $header,ws:  $ws);
        }

        $fc_uuid_id = (new fc_uuid_html(html: $this->html_base))->select_fc_uuid_id(
            cols: 12, con_registros: true, id_selected: $this->row_upd->fc_uuid_id, link: $this->link);

        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar input',data:  $fc_uuid_id, header: $header,ws:  $ws);
        }

        $this->inputs->fc_uuid_id = $fc_uuid_id;

        $cat_sat_motivo_cancelacion_id =
            (new cat_sat_motivo_cancelacion_html(html: $this->html_base))->select_cat_sat_motivo_cancelacion_id(
                cols: 12, con_registros: true, id_selected: $this->row_upd->cat_sat_motivo_cancelacion_id, link: $this->link);

        if(errores::$error){
            return $this->retorno_error(
                mensaje: 'Error al generar input',data:  $cat_sat_motivo_cancelacion_id, header: $header,ws:  $ws);
        }

        $this->inputs->cat_sat_motivo_cancelacion_id = $cat_sat_motivo_cancelacion_id;

        return $r_modifica;
    }


}
