<?php

namespace gamboamartin\facturacion\models;

use base\orm\_modelo_parent_sin_codigo;
use gamboamartin\errores\errores;
use gamboamartin\notificaciones\models\not_mensaje;
use PDO;
use stdClass;


class fc_notificacion extends _notificacion
{
    public function __construct(PDO $link)
    {
        $tabla = 'fc_notificacion';
        $columnas = array($tabla => false, 'fc_factura' => $tabla, 'not_mensaje' => $tabla);
        $campos_obligatorios = array('fc_factura_id', 'not_mensaje_id');

        $columnas_extra = array();



        parent::__construct(link: $link, tabla: $tabla, campos_obligatorios: $campos_obligatorios,
            columnas: $columnas,  columnas_extra: $columnas_extra);

        $this->NAMESPACE = __NAMESPACE__;

        $this->etiqueta = 'Notificaciones';
    }

    public function alta_bd(array $keys_integra_ds = array('descripcion')): array|stdClass
    {

        $this->modelo_entidad = new fc_factura(link: $this->link);

        $r_alta_bd = parent::alta_bd($keys_integra_ds); // TODO: Change the autogenerated stub
        if(errores::$error){
            return $this->error->error(mensaje:'Error al insertar',data:  $r_alta_bd);
        }
        return $r_alta_bd;
    }


}