namespace gamboamartin\notificaciones\controllers;


use gamboamartin\errores\errores;
use gamboamartin\notificaciones\html\not_emisor_html;
use gamboamartin\notificaciones\html\not_mensaje_html;
use gamboamartin\notificaciones\models\not_mensaje;
use gamboamartin\system\_ctl_parent_sin_codigo;
use gamboamartin\system\links_menu;
use gamboamartin\template\html;

use PDO;
use stdClass;
use Throwable;

class controlador_not_mensaje extends _ctl_parent_sin_codigo {

    public function __construct(PDO $link, html $html = new \gamboamartin\template_1\html(),
                                stdClass $paths_conf = new stdClass()){
        $modelo = new not_mensaje(link: $link);
        $html_ = new not_mensaje_html(html: $html);
        $obj_link = new links_menu(link: $link, registro_id: $this->registro_id);

        $datatables = $this->init_datatable();
        if(errores::$error){
            $error = $this->errores->error(mensaje: 'Error al inicializar datatable',data: $datatables);
            print_r($error);
            die('Error');
        }

        parent::__construct(html: $html_, link: $link, modelo: $modelo, obj_link: $obj_link, datatables: $datatables,
            paths_conf: $paths_conf);

        $this->titulo_lista = 'Emisores';
    }

    public function alta(bool $header, bool $ws = false): array|string
    {
        $r_alta = parent::alta(header: $header,ws:  $ws); // TODO: Change the autogenerated stub
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar template',data:  $r_alta, header: $header,ws:  $ws);
        }

        $this->inputs = new stdClass();

        $asunto = (new not_mensaje_html(html: $this->html_base))->input_asunto(cols: 12, row_upd: new stdClass(), value_vacio: false);
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar input',data:  $asunto, header: $header,ws:  $ws);
        }

        $this->inputs->asunto = $asunto;

        $mensaje = (new not_mensaje_html(html: $this->html_base))->input_mensaje(
            cols: 12, row_upd: new stdClass(), value_vacio: false);
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar input',data:  $mensaje, header: $header,ws:  $ws);
        }

        $this->inputs->mensaje = $mensaje;

        $not_emisor_id = (new not_emisor_html(html: $this->html_base))->select_not_emisor_id(
            cols: 12,con_registros: true,id_selected: -1,link:  $this->link);
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar input',data:  $not_emisor_id, header: $header,ws:  $ws);
        }

        $this->inputs->not_emisor_id = $not_emisor_id;


        return $r_alta;
    }
    public function envia_mensaje(bool $header, bool $ws = false): array|string
    {
        $mail =(new not_mensaje(link: $this->link))->envia_mensaje(not_mensaje_id: $this->registro_id);
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al enviar mensaje',data:  $mail, header: $header,ws:  $ws);
        }

        if($header){
            $id_retorno = -1;
            $this->retorno_base(registro_id:$id_retorno, result: $mail, siguiente_view: 'lista',
                ws:  $ws,seccion_retorno: $this->seccion);
        }
        if($ws){
            header('Content-Type: application/json');
            try {
                echo json_encode($mail, JSON_THROW_ON_ERROR);
            }
            catch (Throwable $e){
                $error = (new errores())->error(mensaje: 'Error al maquetar JSON' , data: $e);
                print_r($error);
            }
            exit;
        }

        return $mail;

    }
    final public function init_datatable(): stdClass
    {

        $columns["not_mensaje_id"]["titulo"] = "Id";
        $columns["not_emisor_email"]["titulo"] = "Email Emisor";
        $columns["not_mensaje_asunto"]["titulo"] = "Asunto";
        $columns["not_mensaje_etapa"]["titulo"] = "Etapa";

        $filtro[] = array("not_mensaje.id");
        $filtro[] = array("not_emisor.email");
        $filtro[] = array("not_mensaje.asunto");

        $datatables = new stdClass();
        $datatables->columns = $columns;
        $datatables->filtro = $filtro;

        return $datatables;
    }
    public function modifica(bool $header, bool $ws = false, array $keys_selects = array()): array|stdClass
    {
        $r_modifica = parent::modifica($header, $ws, $keys_selects); // TODO: Change the autogenerated stub
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar template',data:  $r_modifica, header: $header,ws:  $ws);
        }
        $this->inputs = new stdClass();

        $asunto = (new not_mensaje_html(html: $this->html_base))->input_asunto(cols: 12, row_upd: $this->row_upd, value_vacio: false);
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar input',data:  $asunto, header: $header,ws:  $ws);
        }

        $this->inputs->asunto = $asunto;

        $mensaje = (new not_mensaje_html(html: $this->html_base))->input_mensaje(cols: 12, row_upd: $this->row_upd, value_vacio: false);
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar input',data:  $mensaje, header: $header,ws:  $ws);
        }

        $this->inputs->mensaje = $mensaje;

        $not_emisor_id = (new not_emisor_html(html: $this->html_base))->select_not_emisor_id(cols: 12,
            con_registros: true,id_selected: $this->row_upd->not_emisor_id,link:  $this->link);
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar input',data:  $not_emisor_id, header: $header,ws:  $ws);
        }

        $this->inputs->not_emisor_id = $not_emisor_id;


        return $r_modifica;


    }


}