namespace gamboamartin\notificaciones\controllers;


use gamboamartin\errores\errores;

use gamboamartin\notificaciones\html\not_rel_mensaje_etapa_html;
use gamboamartin\notificaciones\html\not_rel_mensaje_html;
use gamboamartin\notificaciones\models\not_rel_mensaje_etapa;
use gamboamartin\proceso\html\pr_etapa_proceso_html;
use gamboamartin\system\_ctl_parent_sin_codigo;
use gamboamartin\system\links_menu;
use gamboamartin\template\html;

use PDO;
use stdClass;
use Throwable;

class controlador_not_rel_mensaje_etapa extends _ctl_parent_sin_codigo {

    public function __construct(PDO $link, html $html = new \gamboamartin\template_1\html(),
                                stdClass $paths_conf = new stdClass()){
        $modelo = new not_rel_mensaje_etapa(link: $link);
        $html_ = new not_rel_mensaje_etapa_html(html: $html);
        $obj_link = new links_menu(link: $link, registro_id: $this->registro_id);

        $datatables = $this->init_datatable();
        if(errores::$error){
            $error = $this->errores->error(mensaje: 'Error al inicializar datatable',data: $datatables);
            print_r($error);
            die('Error');
        }

        parent::__construct(html: $html_, link: $link, modelo: $modelo, obj_link: $obj_link, datatables: $datatables,
            paths_conf: $paths_conf);

        $this->titulo_lista = 'Emisores';
    }

    public function alta(bool $header, bool $ws = false): array|string
    {
        $r_alta = parent::alta(header: $header,ws:  $ws); // TODO: Change the autogenerated stub
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar template',data:  $r_alta, header: $header,ws:  $ws);
        }

        $this->inputs = new stdClass();


        $not_rel_mensaje_id = (new not_rel_mensaje_html(html: $this->html_base))->select_not_rel_mensaje_id(cols: 12,
            con_registros: true,id_selected: -1,link:  $this->link);
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar input',data:  $not_rel_mensaje_id, header: $header,ws:  $ws);
        }

        $this->inputs->not_rel_mensaje_id = $not_rel_mensaje_id;

        $pr_etapa_proceso_id = (new pr_etapa_proceso_html(html: $this->html_base))->select_pr_etapa_proceso_id(cols: 12,
            con_registros: true,id_selected: -1,link:  $this->link);
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar input',data:  $pr_etapa_proceso_id, header: $header,ws:  $ws);
        }

        $this->inputs->pr_etapa_proceso_id = $pr_etapa_proceso_id;


        return $r_alta;
    }



    final public function init_datatable(): stdClass
    {

        $columns["not_rel_mensaje_etapa_id"]["titulo"] = "Id";
        $columns["not_rel_mensaje_etapa_fecha"]["titulo"] = "Fecha";
        $columns["not_rel_mensaje_id"]["titulo"] = "Rel Mensaje Id";
        $columns["pr_etapa_proceso_id"]["titulo"] = "Pr Etapa Proceso Id";

        $filtro[] = array("not_rel_mensaje_etapa.id");
        $filtro[] = array("not_rel_mensaje_etapa.fecha");
        $filtro[] = array("not_rel_mensaje.id");
        $filtro[] = array("pr_etapa_proceso.id");

        $datatables = new stdClass();
        $datatables->columns = $columns;
        $datatables->filtro = $filtro;

        return $datatables;
    }

    public function modifica(bool $header, bool $ws = false, array $keys_selects = array()): array|stdClass
    {
        $r_modifica = parent::modifica($header, $ws, $keys_selects); // TODO: Change the autogenerated stub
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar template',data:  $r_modifica, header: $header,ws:  $ws);
        }
        $this->inputs = new stdClass();

        $not_rel_mensaje_id = (new not_rel_mensaje_html(html: $this->html_base))->select_not_rel_mensaje_id(cols: 12,
            con_registros: true,id_selected: $this->row_upd->not_rel_mensaje_id,link:  $this->link);
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar input',data:  $not_rel_mensaje_id, header: $header,ws:  $ws);
        }

        $this->inputs->not_rel_mensaje_id = $not_rel_mensaje_id;

        $pr_etapa_proceso_id = (new pr_etapa_proceso_html(html: $this->html_base))->select_pr_etapa_proceso_id(cols: 12,
            con_registros: true,id_selected: $this->row_upd->pr_etapa_proceso_id,link:  $this->link);
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar input',data:  $pr_etapa_proceso_id, header: $header,ws:  $ws);
        }

        $this->inputs->pr_etapa_proceso_id = $pr_etapa_proceso_id;


        return $r_modifica;


    }


}