<?php
namespace gamboamartin\notificaciones\models;

use base\orm\_modelo_parent_sin_codigo;

use gamboamartin\errores\errores;
use gamboamartin\proceso\models\pr_etapa_proceso;
use PDO;
use stdClass;

class not_mensaje_etapa extends _modelo_parent_sin_codigo {
    public function __construct(PDO $link){
        $tabla = 'not_mensaje_etapa';
        $columnas = array($tabla=>false,'not_mensaje'=>$tabla,'pr_etapa_proceso'=>$tabla,
            'not_emisor'=>'not_mensaje','pr_etapa'=>'pr_etapa_proceso',
            'pr_proceso'=>'pr_etapa_proceso');
        $campos_obligatorios = array('not_mensaje_id','pr_etapa_proceso_id','fecha');

        $no_duplicados = array();

        $campos_view = array();
        $columnas_extra = array();

        parent::__construct(link: $link, tabla: $tabla, campos_obligatorios: $campos_obligatorios,
            columnas: $columnas, campos_view: $campos_view, columnas_extra: $columnas_extra,
            no_duplicados: $no_duplicados, tipo_campos: array());

        $this->NAMESPACE = __NAMESPACE__;

        $this->etiqueta = 'Mensajes';
    }

    public function alta_bd(array $keys_integra_ds = array('descripcion')): array|stdClass
    {
        $not_mensaje = (new not_mensaje(link: $this->link))->registro(registro_id: $this->registro['not_mensaje_id']);
        if(errores::$error){
            return $this->error->error(mensaje: 'Error al obtener not_mensaje',data: $not_mensaje);
        }

        $pr_etapa_proceso = (new pr_etapa_proceso(link: $this->link))->registro(registro_id: $this->registro['pr_etapa_proceso_id']);
        if(errores::$error){
            return $this->error->error(mensaje: 'Error al obtener not_receptor',data: $pr_etapa_proceso);
        }

        if(!isset($this->registro['fecha'])){
            $fecha = date('Y-m-d');
            $this->registro['fecha'] = $fecha;

        }

        if(!isset($this->registro['descripcion'])){
            $descripcion = $not_mensaje['not_emisor_email'].' '.$not_mensaje['not_mensaje_asunto'].' '.
                $pr_etapa_proceso['pr_etapa_descripcion'].' '.date('YmdHis');
            $this->registro['descripcion'] = $descripcion.$descripcion.mt_rand(100000,99999999);;
        }
        $r_alta_bd = parent::alta_bd(keys_integra_ds: $keys_integra_ds); // TODO: Change the autogenerated stub
        if(errores::$error){
            return $this->error->error(mensaje: 'Error al dar de alta mensaje',data: $r_alta_bd);
        }
        return $r_alta_bd;
    }



}