namespace gamboamartin\organigrama\controllers;

use gamboamartin\errores\errores;
use gamboamartin\organigrama\html\org_actividad_html;
use gamboamartin\organigrama\html\org_tipo_actividad_html;
use gamboamartin\organigrama\models\org_actividad;
use gamboamartin\organigrama\models\org_tipo_actividad;
use gamboamartin\system\_ctl_parent_sin_codigo;
use gamboamartin\system\links_menu;
use gamboamartin\template\html;
use PDO;
use stdClass;

class controlador_org_actividad extends _ctl_parent_sin_codigo {

    public function __construct(PDO $link, html $html = new \gamboamartin\template_1\html(),
                                stdClass $paths_conf = new stdClass()){
        $modelo = new org_actividad(link: $link);
        $html = new org_actividad_html(html: $html);
        $obj_link = new links_menu(link: $link, registro_id:$this->registro_id);

        $datatables = new stdClass();
        $datatables->columns = array();
        $datatables->columns['org_actividad_id']['titulo'] = 'Id';
        $datatables->columns['org_actividad_descripcion']['titulo'] = 'Actividad';
        $datatables->columns['org_tipo_actividad_descripcion']['titulo'] = 'Tipo actividad';

        $datatables->filtro = array();
        $datatables->filtro[] = 'org_actividad.id';
        $datatables->filtro[] = 'org_actividad.descripcion';
        $datatables->filtro[] = 'org_tipo_actividad.descripcion';

        parent::__construct(html:$html, link: $link,modelo:  $modelo, obj_link: $obj_link, datatables: $datatables,
            paths_conf: $paths_conf);

        $this->titulo_lista = 'Actividades';

        $this->childrens_data['org_ejecuta']['title'] = 'Ejecuta';

        $this->parents_verifica['org_tipo_actividad'] = (new org_tipo_actividad(link: $this->link));

        $this->verifica_parents_alta = true;

    }

    public function alta(bool $header, bool $ws = false): array|string
    {
        $r_alta =  parent::alta($header, $ws); // TODO: Change the autogenerated stub
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al maquetar alta',data:  $r_alta, header: $header,ws: $ws);
        }

        $input_tiempo = (new org_actividad_html(html: $this->html_base))->input_numero_int(cols: 12,name: 'tiempo',
            row_upd: new stdClass(), value_vacio: true,place_holder: 'Tiempo',
            title: 'Tiempo en horas mensuales de la actividad promedio');
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al maquetar input',data:  $input_tiempo, header: $header,ws: $ws);
        }

        $select_tipo_actividad = (new org_tipo_actividad_html(html: $this->html_base))->select_org_tipo_actividad_id(
            cols: 12,con_registros: true,id_selected: -1,link: $this->link);
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al maquetar select',data:  $select_tipo_actividad,
                header: $header,ws: $ws);
        }


        $this->inputs->tiempo = $input_tiempo;
        $this->inputs->org_tipo_actividad_id = $select_tipo_actividad;
        return $r_alta;
    }

    protected function campos_view(array $inputs = array()): array
    {
        $keys = new stdClass();
        $keys->inputs = array('codigo','descripcion');
        $keys->selects = array();

        $init_data = array();
        $init_data['org_tipo_actividad'] = "gamboamartin\\organigrama";

        $campos_view = $this->campos_view_base(init_data: $init_data,keys:  $keys);

        if(errores::$error){
            return $this->errores->error(mensaje: 'Error al inicializar campo view',data:  $campos_view);
        }


        return $campos_view;
    }

    protected function key_selects_txt(array $keys_selects): array
    {
        $keys_selects = (new \base\controller\init())->key_select_txt(cols: 6,key: 'codigo', keys_selects:$keys_selects, place_holder: 'Cod');
        if(errores::$error){
            return $this->errores->error(mensaje: 'Error al maquetar key_selects',data:  $keys_selects);
        }

        $keys_selects = (new \base\controller\init())->key_select_txt(cols: 6,key: 'descripcion', keys_selects:$keys_selects, place_holder: 'Actividad');
        if(errores::$error){
            return $this->errores->error(mensaje: 'Error al maquetar key_selects',data:  $keys_selects);
        }
        $keys_selects = (new \base\controller\init())->key_select_txt(cols: 6,key: 'tiempo', keys_selects:$keys_selects, place_holder: 'Tiempo');
        if(errores::$error){
            return $this->errores->error(mensaje: 'Error al maquetar key_selects',data:  $keys_selects);
        }

        $keys_selects = (new \base\controller\init())->key_select_txt(cols: 12,key: 'org_tipo_actividad_id',
            keys_selects:$keys_selects, place_holder: 'Tipo Actividad');
        if(errores::$error){
            return $this->errores->error(mensaje: 'Error al maquetar key_selects',data:  $keys_selects);
        }


        return $keys_selects;
    }

    public function modifica(bool $header, bool $ws = false, array $keys_selects = array()): array|stdClass
    {
        $this->not_actions[] = __FUNCTION__;
        $r_modifica = $this->init_modifica(); // TODO: Change the autogenerated stub
        if(errores::$error){
            return $this->retorno_error(
                mensaje: 'Error al generar salida de template',data:  $r_modifica,header: $header,ws: $ws);
        }



        $keys_selects['descripcion'] = new stdClass();
        $keys_selects['descripcion']->cols = 12;

        $keys_selects['codigo'] = new stdClass();
        $keys_selects['codigo']->disabled = true;

        $base = $this->base_upd(keys_selects: $keys_selects, params: array(),params_ajustados: array());
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al integrar base',data:  $base, header: $header,ws:  $ws);
        }

        $input_tiempo = (new org_actividad_html(html: $this->html_base))->input_numero_int(cols: 12,name: 'tiempo',
            row_upd: $this->row_upd, value_vacio: false,place_holder: 'Tiempo',
            title: 'Tiempo en horas mensuales de la actividad promedio');
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al maquetar input',data:  $input_tiempo, header: $header,ws: $ws);
        }

        $this->inputs->tiempo = $input_tiempo;

        $select_tipo_actividad = (new org_tipo_actividad_html(html: $this->html_base))->select_org_tipo_actividad_id(
            cols: 12,con_registros: true,id_selected: $this->row_upd->org_tipo_actividad_id,link: $this->link);
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al maquetar select',data:  $select_tipo_actividad,
                header: $header,ws: $ws);
        }

        $this->inputs->org_tipo_actividad_id = $select_tipo_actividad;


        return $r_modifica;
    }




}
