namespace gamboamartin\organigrama\controllers;

use gamboamartin\errores\errores;
use gamboamartin\organigrama\html\org_departamento_html;
use gamboamartin\organigrama\html\org_puesto_html;
use gamboamartin\organigrama\html\org_tipo_puesto_html;
use gamboamartin\organigrama\models\org_clasificacion_dep;
use gamboamartin\organigrama\models\org_departamento;
use gamboamartin\organigrama\models\org_empresa;
use gamboamartin\system\_ctl_parent_sin_codigo;
use gamboamartin\system\links_menu;
use gamboamartin\template\html;
use PDO;
use stdClass;

class controlador_org_departamento extends _ctl_parent_sin_codigo {

    public array|stdClass $keys_selects = array();
    public string $link_org_puesto_alta_bd = '';

    public function __construct(PDO $link, html $html = new \gamboamartin\template_1\html(),
                                stdClass $paths_conf = new stdClass()){
        $modelo = new org_departamento(link: $link);
        $html_ = new org_departamento_html($html);
        $obj_link = new links_menu(link: $link, registro_id:  $this->registro_id);

        $columns["org_departamento_id"]["titulo"] = "Id";
        $columns["org_departamento_codigo"]["titulo"] = "Codigo";
        $columns["org_departamento_descripcion"]["titulo"] = "Descripcion";
        $columns["org_clasificacion_dep_descripcion"]["titulo"] = "Clasificacion";
        $columns["org_empresa_descripcion"]["titulo"] = "Empresa";

        $filtro = array("org_departamento.id","org_departamento.codigo","org_departamento.descripcion",
            "org_clasificacion_dep.descripcion", "rg_empresa.descripcion");

        $datatables = new stdClass();
        $datatables->columns = $columns;
        $datatables->filtro = $filtro;

        parent::__construct(html:$html_, link: $link,modelo:  $modelo, obj_link: $obj_link, datatables: $datatables,
            paths_conf: $paths_conf);

        $this->titulo_lista = 'Departamentos';

        $link_org_puesto_alta_bd = $this->obj_link->link_alta_bd(link: $link, seccion: 'org_puesto');
        if(errores::$error){
            $error = $this->errores->error(mensaje: 'Error al obtener link',data:  $link_org_puesto_alta_bd);
            print_r($error);
            exit;
        }
        $this->link_org_puesto_alta_bd = $link_org_puesto_alta_bd;

        $this->childrens_data['org_puesto']['title'] = 'Puesto';

        $this->parents_verifica['org_clasificacion_dep'] = (new org_clasificacion_dep(link: $this->link));
        $this->parents_verifica['org_empresa'] = (new org_empresa(link: $this->link));

        $this->verifica_parents_alta = true;


    }

    public function alta(bool $header, bool $ws = false): array|string
    {
        $r_alta = $this->init_alta();
        if(errores::$error){
            return $this->retorno_error(
                mensaje: 'Error al inicializar alta',data:  $r_alta, header: $header,ws:  $ws);
        }

        $keys_selects = $this->key_select(cols:12, con_registros: true,filtro:  array(), key: 'org_clasificacion_dep_id',
            keys_selects: array(), id_selected: -1, label: 'Clasificacion');
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al maquetar key_selects',data:  $keys_selects, header: $header,ws:  $ws);
        }

        $keys_selects = $this->key_select(cols:12, con_registros: true,filtro:  array(), key: 'org_empresa_id',
            keys_selects: $keys_selects, id_selected: -1, label: 'Empresa');
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al maquetar key_selects',data:  $keys_selects, header: $header,ws:  $ws);
        }

        $keys_selects['descripcion'] = new stdClass();
        $keys_selects['descripcion']->cols = 12;

        $inputs = $this->inputs(keys_selects: $keys_selects);
        if(errores::$error){
            return $this->retorno_error(
                mensaje: 'Error al obtener inputs',data:  $inputs, header: $header,ws:  $ws);
        }


        return $r_alta;
    }

    public function alta_bd(bool $header, bool $ws = false): array|stdClass
    {
        $this->link->beginTransaction();

        $retorno = (new _base())->data_retorno(tabla: $this->tabla);
        if(errores::$error){
            $this->link->rollBack();
            return $this->retorno_error(mensaje: 'Error al obtener datos de retorno',
                data:  $retorno, header: $header,ws:$ws);
        }


        $r_alta_bd = parent::alta_bd(header: false); // TODO: Change the autogenerated stub
        if(errores::$error){
            $this->link->rollBack();
            return $this->retorno_error(mensaje: 'Error al dar de alta empresa',data:  $r_alta_bd, header: $header,ws:$ws);
        }
        $this->link->commit();


        $header_exe = (new _base())->header(controler: $this, header: $header,result:  $r_alta_bd,retorno:  $retorno,ws:  $ws);
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al obtener retorno',data:  $header_exe, header: $header,ws:$ws);
        }

        return $r_alta_bd;
    }


    protected function campos_view(array $inputs = array()): array
    {
        $keys = new stdClass();
        $keys->inputs = array('codigo','descripcion');
        $keys->selects = array();

        $init_data = array();
        $init_data['org_clasificacion_dep'] = "gamboamartin\\organigrama";
        $init_data['org_empresa'] = "gamboamartin\\organigrama";
        $campos_view = $this->campos_view_base(init_data: $init_data,keys:  $keys);

        if(errores::$error){
            return $this->errores->error(mensaje: 'Error al inicializar campo view',data:  $campos_view);
        }


        return $campos_view;
    }

    protected function inputs_children(stdClass $registro): array|stdClass
    {

        $select_org_departamento_id = (new org_departamento_html(html: $this->html_base))->select_org_departamento_id(
            cols:12,con_registros: true,id_selected:  -1,link:  $this->link);
        if(errores::$error){
            return $this->errores->error(mensaje: 'Error al obtener select_org_departamento_id',data:  $select_org_departamento_id);
        }

        $select_org_tipo_puesto_id = (new org_tipo_puesto_html(html: $this->html_base))->select_org_tipo_puesto_id(
            cols:12,con_registros: true,id_selected:  -1,link:  $this->link);
        if(errores::$error){
            return $this->errores->error(mensaje: 'Error al obtener select_org_tipo_puesto_id',data:  $select_org_tipo_puesto_id);
        }

        $org_puesto_descripcion = (new org_puesto_html(html: $this->html_base))->input_descripcion(
            cols:12,row_upd:  new stdClass(), value_vacio: true, place_holder: 'Puesto');
        if(errores::$error){
            return $this->errores->error(mensaje: 'Error al obtener org_puesto_descripcion',
                data:  $org_puesto_descripcion);
        }


        $this->inputs = new stdClass();
        $this->inputs->select = new stdClass();
        $this->inputs->select->org_departamento_id = $select_org_departamento_id;
        $this->inputs->select->org_tipo_puesto_id = $select_org_tipo_puesto_id;
        $this->inputs->org_puesto_descripcion = $org_puesto_descripcion;

        return $this->inputs;
    }

    protected function key_selects_txt(array $keys_selects): array
    {

        $keys_selects = (new \base\controller\init())->key_select_txt(cols: 6, key: 'codigo', keys_selects: $keys_selects, place_holder: 'Cod');
        if (errores::$error) {
            return $this->errores->error(mensaje: 'Error al maquetar key_selects', data: $keys_selects);
        }

        $keys_selects = (new \base\controller\init())->key_select_txt(cols: 6,key: 'descripcion', keys_selects:$keys_selects, place_holder: 'Departamento');
        if(errores::$error){
            return $this->errores->error(mensaje: 'Error al maquetar key_selects',data:  $keys_selects);
        }

        return $keys_selects;
    }

    public function modifica(bool $header, bool $ws = false, array $keys_selects = array()): array|stdClass
    {
        $this->not_actions[] = __FUNCTION__;
        $r_modifica = $this->init_modifica(); // TODO: Change the autogenerated stub
        if(errores::$error){
            return $this->retorno_error(
                mensaje: 'Error al generar salida de template',data:  $r_modifica,header: $header,ws: $ws);
        }


        $keys_selects = $this->key_select(cols:12, con_registros: true,filtro:  array(), key: 'org_clasificacion_dep_id',
            keys_selects: array(), id_selected: $this->registro['org_clasificacion_dep_id'], label: 'Clasificacion');
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al maquetar key_selects',data:  $keys_selects, header: $header,ws:  $ws);
        }

        $keys_selects = $this->key_select(cols:12, con_registros: true,filtro:  array(), key: 'org_empresa_id',
            keys_selects: $keys_selects, id_selected: $this->registro['org_empresa_id'], label: 'Empresa');
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al maquetar key_selects',data:  $keys_selects, header: $header,ws:  $ws);
        }

        $keys_selects['descripcion'] = new stdClass();
        $keys_selects['descripcion']->cols = 12;

        $keys_selects['codigo'] = new stdClass();
        $keys_selects['codigo']->disabled = true;

        $base = $this->base_upd(keys_selects: $keys_selects, params: array(),params_ajustados: array());
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al integrar base',data:  $base, header: $header,ws:  $ws);
        }




        return $r_modifica;
    }

    public function puestos(bool $header = true, bool $ws = false): array|stdClass|string
    {

        $data_view = new stdClass();
        $data_view->names = array('Id','Puesto','Tipo Puesto','Acciones');
        $data_view->keys_data = array('org_puesto_id','org_puesto_descripcion',
            'org_tipo_puesto_id');
        $data_view->key_actions = 'acciones';
        $data_view->namespace_model = 'gamboamartin\\organigrama\\models';
        $data_view->name_model_children = 'org_puesto';

        $contenido_table = $this->contenido_children(data_view: $data_view, next_accion: __FUNCTION__,
            not_actions: $this->not_actions);
        if(errores::$error){
            return $this->retorno_error(
                mensaje: 'Error al obtener tbody',data:  $contenido_table, header: $header,ws:  $ws);
        }


        return $contenido_table;

    }



}
