namespace gamboamartin\organigrama\controllers;

use gamboamartin\errores\errores;
use gamboamartin\organigrama\html\org_dependencia_html;
use gamboamartin\organigrama\html\org_puesto_html;
use gamboamartin\organigrama\models\org_dependencia;
use gamboamartin\organigrama\models\org_puesto;
use gamboamartin\system\links_menu;
use gamboamartin\system\system;
use gamboamartin\template\html;

use PDO;
use stdClass;

class controlador_org_dependencia extends system {

    public function __construct(PDO $link, html $html = new \gamboamartin\template_1\html(),
                                stdClass $paths_conf = new stdClass()){
        $modelo = new org_dependencia(link: $link);
        $html = new org_dependencia_html($html);
        $obj_link = new links_menu(link: $link, registro_id:$this->registro_id);

        $datatables = new stdClass();
        $datatables->columns = array();
        $datatables->columns['org_dependencia_id']['titulo'] = 'Id';
        $datatables->columns['org_dependencia_descripcion']['titulo'] = 'Dependencia';
        $datatables->columns['org_puesto_empleado_descripcion']['titulo'] = 'Puesto Empleado';
        $datatables->columns['org_puesto_jefe_descripcion']['titulo'] = 'Puesto Jefe';

        $datatables->filtro = array();
        $datatables->filtro[] = 'org_dependencia.id';
        $datatables->filtro[] = 'org_dependencia.descripcion';
        $datatables->filtro[] = 'org_puesto_empleado.descripcion';
        $datatables->filtro[] = 'org_puesto_jefe.descripcion';
        parent::__construct(html:$html, link: $link,modelo:  $modelo, obj_link: $obj_link, datatables: $datatables,
            paths_conf: $paths_conf);

        $this->titulo_lista = 'Dependencias';

        $this->parents_verifica['org_puesto'] = (new org_puesto(link: $this->link));

        $this->verifica_parents_alta = true;




    }

    public function alta(bool $header, bool $ws = false): array|string
    {
        $r_alta =  parent::alta(header: false, ws: false); // TODO: Change the autogenerated stub
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar template',data:  $r_alta, header: $header,ws:$ws);
        }

        $this->inputs->select = new stdClass();
        $select = (new org_puesto_html(html: $this->html_base))->select_org_puesto_id(cols:12,con_registros:true,id_selected:-1,
            link: $this->link, label: "Puesto Jefe");
        if(errores::$error){
            $error = $this->errores->error(mensaje: 'Error al generar select',data:  $select);
            print_r($error);
            die('Error');
        }

        $this->inputs->select->org_puesto_jefe_id = $select;

        $select = (new org_puesto_html(html: $this->html_base))->select_org_puesto_id(cols:12,con_registros:true,id_selected:-1,
            link: $this->link, label: "Puesto Empleado");
        if(errores::$error){
            $error = $this->errores->error(mensaje: 'Error al generar select',data:  $select);
            print_r($error);
            die('Error');
        }
        $this->inputs->select->org_puesto_empleado_id = $select;

        $in_descripcion_select = (new org_dependencia_html(html: $this->html_base))->input(cols: 6,row_upd:  new stdClass(),value_vacio:  true, campo: "descripcion_select");

        if(errores::$error){
            $error = $this->errores->error(mensaje: 'Error al generar el input',data:  $in_descripcion_select);
            print_r($error);
            die('Error');
        }

        $this->inputs->descripcion_select = $in_descripcion_select;
        return $r_alta;
    }

    public function modifica(bool $header, bool $ws = false, array $keys_selects = array()): array|stdClass
    {
        $r_modifica = parent::modifica($header, $ws); // TODO: Change the autogenerated stub
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar template',data:  $r_modifica, header: $header,ws:$ws);
        }

        $this->inputs->select = new stdClass();
        $select = (new org_puesto_html(html: $this->html_base))->select_org_puesto_id(cols:12,con_registros:true,
            id_selected: $this->row_upd->org_puesto_jefe_id,link: $this->link, label: "Puesto Jefe");
        if(errores::$error){
            $error = $this->errores->error(mensaje: 'Error al generar select',data:  $select);
            print_r($error);
            die('Error');
        }

        $this->inputs->select->org_puesto_jefe_id = $select;

        $select = (new org_puesto_html(html: $this->html_base))->select_org_puesto_id(cols:12,con_registros:true,
            id_selected:$this->row_upd->org_puesto_empleado_id,link: $this->link, label: "Puesto Empleado");
        if(errores::$error){
            $error = $this->errores->error(mensaje: 'Error al generar select',data:  $select);
            print_r($error);
            die('Error');
        }
        $this->inputs->select->org_puesto_empleado_id = $select;

        return $r_modifica;

    }


}
