namespace gamboamartin\organigrama\controllers;

use gamboamartin\errores\errores;
use gamboamartin\organigrama\html\org_empresa_html;
use gamboamartin\organigrama\html\org_logo_html;
use gamboamartin\organigrama\models\org_logo;
use gamboamartin\system\_ctl_parent_sin_codigo;
use gamboamartin\system\links_menu;

use gamboamartin\template\html;

use PDO;
use stdClass;

class controlador_org_logo extends _ctl_parent_sin_codigo {

    public array|stdClass $keys_selects = array();

    public function __construct(PDO $link, html $html = new \gamboamartin\template_1\html(),
                                stdClass $paths_conf = new stdClass()){
        $modelo = new org_logo(link: $link);
        $html = new org_logo_html(html: $html);
        $obj_link = new links_menu(link: $link, registro_id:$this->registro_id);

        $columns["org_logo_id"]["titulo"] = "Id";
        $columns["org_logo_codigo"]["titulo"] = "CÃ³digo";
        $columns["org_logo_descripcion"]["titulo"] = "Logo";

        $filtro = array("org_logo.id","org_logo.codigo","org_logo.descripcion");

        $datatables = new stdClass();
        $datatables->columns = $columns;
        $datatables->filtro = $filtro;

        parent::__construct(html:$html, link: $link,modelo:  $modelo, obj_link: $obj_link, datatables: $datatables,
            paths_conf: $paths_conf);

        $this->titulo_lista = 'Logos';


    }

    public function alta(bool $header, bool $ws = false): array|string
    {
        $r_alta = parent::alta(header: $header,ws:  $ws); // TODO: Change the autogenerated stub
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar template',data:  $r_alta, header: $header,ws:  $ws);
        }
        $org_empresa_id = (new org_empresa_html(html: $this->html_base))->select_org_empresa_id(cols: 12,
            con_registros: true,id_selected: -1,link: $this->link);

        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar org_empresa_id',data:  $org_empresa_id, header: $header,ws:  $ws);
        }

        $this->inputs->org_empresa_id = $org_empresa_id;

        $logo = (new org_logo_html(html: $this->html_base))->input_logo(cols: 12);

        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar logo',data:  $logo, header: $header,ws:  $ws);
        }

        $this->inputs->logo = $logo;

        return $r_alta;

    }


}
