namespace gamboamartin\organigrama\controllers;

use gamboamartin\errores\errores;
use gamboamartin\organigrama\html\org_empresa_html;
use gamboamartin\organigrama\html\org_representante_asignado_html;
use gamboamartin\organigrama\html\org_representante_legal_html;
use gamboamartin\organigrama\models\org_representante_asignado;
use gamboamartin\system\links_menu;
use gamboamartin\system\system;
use gamboamartin\template\html;
use PDO;
use stdClass;

class controlador_org_representante_asignado extends system {

    public function __construct(PDO $link, html $html = new \gamboamartin\template_1\html(), stdClass $paths_conf = new stdClass()){
        $modelo = new org_representante_asignado(link: $link);
        $html = new org_representante_asignado_html($html);
        $obj_link = new links_menu(link: $link, registro_id:$this->registro_id);

        $datatables = new stdClass();
        $datatables->columns = array();
        $datatables->columns['org_representante_asignado_id']['titulo'] = 'Id';
        $datatables->columns['org_representante_asignado_descripcion']['titulo'] = 'Representante Asignado';
        $datatables->columns['org_representante_legal_descripcion']['titulo'] = 'Representante Legal';
        $datatables->columns['org_empresa_descripcion']['titulo'] = 'Empresa';

        $datatables->filtro = array();
        $datatables->filtro[] = 'org_representante_asignado.id';
        $datatables->filtro[] = 'org_representante_asignado.descripcion';
        $datatables->filtro[] = 'org_representante_legal.descripcion';
        $datatables->filtro[] = 'org_empresa.descripcion';
        parent::__construct(html:$html, link: $link,modelo:  $modelo, obj_link: $obj_link, datatables: $datatables,
            paths_conf: $paths_conf);

        $this->titulo_lista = 'Actividades';

    }

    public function alta(bool $header, bool $ws = false): array|string
    {
        $r_alta =  parent::alta(header: false, ws: false); // TODO: Change the autogenerated stub
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar template',data:  $r_alta, header: $header,ws:$ws);
        }

        $this->inputs->select = new stdClass();
        $select = (new org_representante_legal_html(html: $this->html_base))->select_org_representante_legal_id(cols:12,con_registros:true,id_selected:-1,link: $this->link);
        if(errores::$error){
            $error = $this->errores->error(mensaje: 'Error al generar select',data:  $select);
            print_r($error);
            die('Error');
        }


        $this->inputs->select->org_representante_legal_id = $select;

        $select = (new org_empresa_html(html: $this->html_base))->select_org_empresa_id(cols:12,con_registros:true,id_selected:-1,link: $this->link);
        if(errores::$error){
            $error = $this->errores->error(mensaje: 'Error al generar select',data:  $select);
            print_r($error);
            die('Error');
        }


        $this->inputs->select->org_empresa_id = $select;

        $in_fecha_inicio = (new org_representante_asignado_html(html: $this->html_base))->input(cols: 6,row_upd:  new stdClass(),value_vacio:  true, campo: "Fecha Inicio");
        $in_fecha_fin = (new org_representante_asignado_html(html: $this->html_base))->input(cols: 6,row_upd:  new stdClass(),value_vacio:  true, campo: "Fecha Fin");

        if(errores::$error){
            $error = $this->errores->error(mensaje: 'Error al generar el input',data:  $in_fecha_inicio);
            print_r($error);
            die('Error');
        }

        $this->inputs->fecha_inicio = $in_fecha_inicio;
        $this->inputs->fecha_fin = $in_fecha_fin;


        return $r_alta;

    }

    public function modifica(bool $header, bool $ws = false, array $keys_selects = array()): array|stdClass
    {
        $r_modifica = parent::modifica($header, $ws); // TODO: Change the autogenerated stub
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar template',data:  $r_modifica, header: $header,ws:$ws);
        }

        $this->inputs->select = new stdClass();

        $select = (new org_representante_legal_html(html: $this->html_base))->select_org_representante_legal_id(cols:12,
            con_registros:true,id_selected:$this->row_upd->org_representante_legal_id,link: $this->link);
        if(errores::$error){
            $error = $this->errores->error(mensaje: 'Error al generar select',data:  $select);
            print_r($error);
            die('Error');
        }
        $this->inputs->select->org_representante_legal_id = $select;

        $select = (new org_empresa_html(html: $this->html_base))->select_org_empresa_id(cols:12,con_registros:true,
            id_selected:$this->row_upd->org_empresa_id,link: $this->link);
        if(errores::$error){
            $error = $this->errores->error(mensaje: 'Error al generar select',data:  $select);
            print_r($error);
            die('Error');
        }
        $this->inputs->select->org_empresa_id = $select;

        $in_fecha_inicio = (new org_representante_asignado_html(html: $this->html_base))->input(cols: 6,row_upd: $this->row_upd,value_vacio:  true, campo: "Fecha Inicio");
        if(errores::$error){
            $error = $this->errores->error(mensaje: 'Error al generar el input',data:  $in_fecha_inicio);
            print_r($error);
            die('Error');
        }

        $in_fecha_fin = (new org_representante_asignado_html(html: $this->html_base))->input(cols: 6,row_upd:  $this->row_upd,value_vacio:  true, campo: "Fecha Fin");
        if(errores::$error){
            $error = $this->errores->error(mensaje: 'Error al generar el input',data:  $in_fecha_fin);
            print_r($error);
            die('Error');
        }

        $this->inputs->fecha_inicio = $in_fecha_inicio;
        $this->inputs->fecha_fin = $in_fecha_fin;

        return $r_modifica;

    }


}
